def create_default_tag_categories(apps, schema_editor):
    TagCategory = apps.get_model("main", "TagCategory")
    Tag = apps.get_model("main", "Tag")
    for category in tagsAndCategories["categories"]:
        categoryObj = TagCategory.objects.create(name=category)
        for tag in tagsAndCategories["tags"][category]:
            Tag.objects.create(name=tag, category=categoryObj)


def remove_default_tag_categories(apps, schema_editor):
    TagCategory = apps.get_model("main", "TagCategory")
    Tag = apps.get_model("main", "Tag")
    for category in tagsAndCategories["categories"]:
        try:
            categoryObj = TagCategory.objects.get(name=category)
            for tag in tagsAndCategories["tags"][category]:
                Tag.objects.filter(name=tag, category=categoryObj).delete()
            categoryObj.delete()
        except ObjectDoesNotExist:
            pass


